import React, { useState } from 'react';

// Ce composant aura aussi besoin des données
function LibraryView({ navigateTo, allCards, itemsData }) {

  const [selectedItem, setSelectedItem] = useState('');

  // Même logique de tri que pour HomeView
  const sortedItems = Array.from(new Set(allCards.map(c => c.deck).filter(Boolean)))
    .sort((a, b) => parseInt(a.split(':')[0], 10) - parseInt(b.split(':')[0], 10));
  sortedItems.unshift("500: Divers");

  const handleGenerateQuiz = () => {
    // La logique du quiz viendra ici plus tard
    alert(`Lancement du quiz pour l'item : ${selectedItem}`);
  };

  const handleGenerateCards = () => {
    // La logique de génération de cartes viendra ici plus tard
    alert(`Génération de cartes pour l'item : ${selectedItem}`);
  };

  return (
    <div id="library-view">
        <div className="library-header">
            <button onClick={() => navigateTo('home')}>← Accueil</button>
            <div className="library-selector-container">
                <label htmlFor="library-item-selector">Consulter l'item :</label>
                <select 
                  id="library-item-selector" 
                  value={selectedItem} 
                  onChange={e => setSelectedItem(e.target.value)}
                >
                    <option value="">-- Choisir un item --</option>
                    {sortedItems.map(item => (
                        <option key={item} value={item}>{item}</option>
                    ))}
                </select>
            </div>
        </div>
        <div id="library-content-display">
            {/* Affichage conditionnel : on montre le texte si un item est sélectionné */}
            {selectedItem ? (
                <p>{itemsData[selectedItem] || 'Aucune note enregistrée pour cet item.'}</p>
            ) : (
                <p className="placeholder-text">Veuillez sélectionner un item pour afficher son contenu.</p>
            )}
        </div>
        <div id="library-actions-container">
            {/* On affiche les boutons d'action seulement si un item avec du contenu est sélectionné */}
            {selectedItem && itemsData[selectedItem] && (
                <>
                    <button className="ia-btn" onClick={handleGenerateQuiz}>S'entraîner (Quiz IA)</button>
                    <button className="ia-btn success" onClick={handleGenerateCards}>Créer des cartes (IA)</button>
                </>
            )}
        </div>
    </div>
  );
}

export default LibraryView;
